mapper.select:
select * from free
where 1 = 1 
	
		 and subject like #{search}
	
	
		 and contents like #{search}
	
	
		 and name like #{search}
	
	
		 and registered_id like #{search}
	
order by no desc
limit
	#{cri.pageStart},#{cri.perPageNum}

select
	count(no)
from
	free
where 1=1
	
		 and subject like #{search}
	
	
		 and contents like #{search}
	
	
		 and name like #{search}
	
	
		 and id like #{search}
